package me.champeau.gradle.deps;

import org.gradle.api.Project;
import org.gradle.api.Plugin;

/**
 * Registers a dependency dump task on a project.
 */
public class GradleDependenciesDumpPlugin implements Plugin<Project> {
    public void apply(Project project) {
        project.getTasks().register("generateDependencyGraph", GenerateDependencyGraph.class, task -> {
            task.getJsonFile().convention(project.getLayout().getBuildDirectory().file("dependency-graph.json"));
            project.getConfigurations().all(conf -> {
                if (conf.isCanBeResolved()) {
                    task.getConfigurations().add(conf);
                }
            });
        });
    }
}
